Sprite *new_debug_object()
{
    Image **images = malloc(sizeof(Image *) * FRAME_COUNT);
    for (int i = 0; i < FRAME_COUNT; i++)
    {
        images[i] = new_image(FRAME_WIDTH, FRAME_HEIGHT);
    }

    for (int frame = 0; frame < FRAME_COUNT; frame++)
    {
        for (int y = 0; y < FRAME_HEIGHT; y++)
        {
            for (int x = 0; x < FRAME_WIDTH; x++)
            {
                images[frame]->pixels[y][x] = *new_pixel(new_piskel_data[frame][y * FRAME_HEIGHT + x]);
            }
        }
    }

    Sprite *sprite = malloc(sizeof(images));
    sprite->images = images;

    return sprite;
}